import React from 'react';
import { AppBar, Toolbar, Button, Typography, Box, Container } from '@mui/material';
import { Link } from 'react-router-dom';
import './Home.css';
import logo from '../Images/new_logo_image.png';

const Home = () => {
  return (
    <div className="home">
      {/* Navbar */}
      <AppBar position="static" sx={{ backgroundColor: '#d84315', borderRadius: '30px', marginBottom: '20px' }}>
        <Toolbar className="toolbar" sx={{ justifyContent: 'space-between' }}>
          {/* Navigation Buttons */}
          <Box>
            <Button
              component={Link}
              to="/"
              color="inherit"
              sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
            >
              Home
            </Button>
            <Button
              component={Link}
              to="/blogs"
              color="inherit"
              sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
            >
              Blogs
            </Button>
            <Button
              component={Link}
              to="/about"
              color="inherit"
              sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
            >
              About
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Centered Logo and Content Side-by-Side */}
      <Container maxWidth="lg">
        <Box
          display="flex"
          flexDirection={{ xs: 'column', md: 'row' }}  // Stack on mobile, row on larger screens
          justifyContent="space-between"
          alignItems="center"
          sx={{ height: '70vh', padding: '20px' }}
        >
          {/* Logo on the left */}
          <Box
            sx={{ 
              flexBasis: '40%', 
              textAlign: { xs: 'center', md: 'left' }, // Center on mobile, align left on desktop
              marginBottom: { xs: '20px', md: '0' }  // Add margin below on mobile for spacing
            }}
          >
            <img 
              src={logo} 
              alt="Logo" 
              style={{ 
                maxWidth: '100%', 
                height: 'auto', 
                maxHeight: '500px' // Ensure the logo doesn't get too large
              }} 
            />
          </Box>

          {/* Content on the right */}
          <Box
            sx={{ 
              flexBasis: '50%', 
              textAlign: { xs: 'center', md: 'right' } // Center text on mobile, align right on desktop
            }}
          >
            <Typography variant="h3" component="h1" gutterBottom>
              Her Power, Her Past
            </Typography>
            <Typography variant="body1" color="textSecondary">
              The lives of women weaved into the crevices of history.
            </Typography>
          </Box>
        </Box>
      </Container>
    </div>
  );
};

export default Home;
