import React from 'react';
import { AppBar, Toolbar, Button, Typography, Box, Container } from '@mui/material';
import { Link } from 'react-router-dom';
import './Blogs.css';

const Blogs = () => {
  return (
    <div className="blogs">
      {/* Navbar */}
      <AppBar position="static" sx={{ backgroundColor: '#d84315', borderRadius: '30px', marginBottom: '20px' }}>
        <Toolbar className="toolbar" sx={{ justifyContent: 'space-between' }}>
          {/* Navigation Buttons */}
          <Box>
            <Button
              component={Link}
              to="/"
              color="inherit"
              sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
            >
              Home
            </Button>
            <Button
              component={Link}
              to="/blogs"
              color="inherit"
              sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
            >
              Blogs
            </Button>
            <Button
              component={Link}
              to="/about"
              color="inherit"
              sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
            >
              About
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Content Section */}
      <Container maxWidth="lg">
        <Box
          display="flex"
          flexDirection={{ xs: 'column', md: 'row' }} // Stack on mobile, row on larger screens
          justifyContent="space-between"
          alignItems="center"
          sx={{ height: '70vh', padding: '20px' }} // Match the Home layout height
        >
          {/* Blog Content */}
          <Box
            sx={{
              flexBasis: '100%', // Ensure the content takes up full width
              textAlign: { xs: 'center', md: 'left' },
            }}
          >
            <Typography variant="h3" component="h1" gutterBottom>
              Welcome to the Blogs Page
            </Typography>
            <Typography variant="body1" color="textSecondary">
              Here you will find a collection of articles and blog posts on various topics.
            </Typography>

            {/* You can add your list of blog articles or posts here */}
            <Box mt={4}>
              <Typography variant="h5" component="h2">
                Blog Post 1
              </Typography>
              <Typography variant="body2" color="textSecondary">
                This is a brief description of blog post 1. It can be about any topic.
              </Typography>

              <Typography variant="h5" component="h2" sx={{ mt: 4 }}>
                Blog Post 2
              </Typography>
              <Typography variant="body2" color="textSecondary">
                This is a brief description of blog post 2. It can be about any topic.
              </Typography>

              {/* Add more blog posts as needed */}
            </Box>
          </Box>
        </Box>
      </Container>
    </div>
  );
};

export default Blogs;
