import React from 'react';
import { AppBar, Toolbar, Button, Typography, Box, Container } from '@mui/material';
import { Link } from 'react-router-dom';
import './Home.css';
import logo from '../Images/new_logo_image.png';

const Home = () => {
  return (
    <div className="home">
      {/* Container for full-page layout */}
      <Box display="flex" sx={{ height: '100vh' }}>
        {/* Left Half - Logo */}
        <Box
          sx={{
            flexBasis: '50%',
            backgroundColor: '#f3f4f6', // Soft background color to contrast text
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <img
            src={logo}
            alt="Logo"
            style={{
              maxWidth: '80%', // Ensures logo doesn’t exceed 80% of the left container
              height: 'auto',
            }}
          />
        </Box>

        {/* Right Half - Navbar and Content */}
        <Box
          sx={{
            flexBasis: '50%',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'flex-start',
            padding: '20px',
          }}
        >
          {/* Navbar positioned at top-right */}
          <AppBar
            position="static"
            elevation={0} // Removes shadow for a flat, modern look
            sx={{
              backgroundColor: 'transparent', // Transparent background
              boxShadow: 'none',
              marginBottom: '20px',
            }}
          >
            <Toolbar sx={{ justifyContent: 'flex-end' }}>
              <Button
                component={Link}
                to="/"
                color="inherit"
                sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
              >
                Home
              </Button>
              <Button
                component={Link}
                to="/blogs"
                color="inherit"
                sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
              >
                Blogs
              </Button>
              <Button
                component={Link}
                to="/about"
                color="inherit"
                sx={{ fontSize: '1.2rem', padding: '10px 20px', marginLeft: '15px' }}
              >
                About
              </Button>
            </Toolbar>
          </AppBar>

          {/* Content - Title and Text */}
          <Box
            sx={{
              flex: 1,
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center', // Vertically center the text
              padding: '20px',
            }}
          >
            <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
              Her Power, Her Past
            </Typography>
            <Typography variant="body1" color="textSecondary" sx={{ fontSize: '1.2rem', maxWidth: '75%' }}>
              The lives of women weaved into the crevices of history. Explore untold stories and discover the
              extraordinary influence of women throughout time.
            </Typography>
          </Box>
        </Box>
      </Box>
    </div>
  );
};

export default Home;
