import React from 'react';
import { Typography, Box, Container, Link, Grid, CardMedia, Divider } from '@mui/material';
import Navbar from '../Components/Navbar';
import './Blogs.css';

const Blogs = () => {
  const blogPosts = [
    {
      id: 1,
      title: "The Impact of Women in Technology",
      summary: "Explore the significant contributions of women in the tech industry and their ongoing challenges.",
      link: "/blogs/the-impact-of-women-in-technology",
      imageUrl: "/path-to-your-image.jpg" // Update this path accordingly
    }
    // More blog posts can be added here
  ];

  return (
    <div className="home">
      <Navbar />

      <Container maxWidth="lg">
        <Box className="centered-box">
          {blogPosts.map((post, index) => (
            <Box key={post.id}>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={4}>
                  <CardMedia
                    component="img"
                    image={post.imageUrl}
                    alt={post.title}
                    sx={{ width: 100, height: 100 }}  // Adjust size as needed
                  />
                </Grid>
                <Grid item xs={8}>
                  <Typography variant="h5" gutterBottom>
                    <Link href={post.link} color="inherit" style={{ textDecoration: 'none' }}>
                      {post.title}
                    </Link>
                  </Typography>
                  <Typography variant="body2">
                    {post.summary}
                  </Typography>
                </Grid>
              </Grid>
              {index !== blogPosts.length - 1 && <Divider sx={{ my: 2 }} />}  // Divider after each post except the last one
            </Box>
          ))}
        </Box>
      </Container>
    </div>
  );
};

export default Blogs;
