import React from 'react';
import { Typography, Box, Container, Link } from '@mui/material';
import Navbar from '../Components/Navbar';
import './Blogs.css';

const Blogs = () => {
  const blogPosts = [
    {
      id: 1,
      title: "The Impact of Women in Technology",
      summary: "Explore the significant contributions of women in the tech industry and their ongoing challenges.",
      link: "/blogs/the-impact-of-women-in-technology"
    }
    // More blog posts can be added here
  ];

  return (
    <div className="home">
      <Navbar /> {/* Reusing the shared Navbar component */}

      <Container maxWidth="lg">
        <Box className="centered-box">
          <Typography variant="h3" component="h1" gutterBottom className="main-title">
            Her Power, Her Past
          </Typography>
          <Typography variant="body1" className="description-text">
            The lives of women weaved into the crevices of history.
          </Typography>
          {blogPosts.map(post => (
            <Box key={post.id} sx={{ marginTop: 2 }}>
              <Typography variant="h5" gutterBottom>
                <Link href={post.link} color="inherit" style={{ textDecoration: 'none' }}>
                  {post.title}
                </Link>
              </Typography>
              <Typography variant="body2">
                {post.summary}
              </Typography>
            </Box>
          ))}
        </Box>
      </Container>
    </div>
  );
};

export default Blogs;
