import React from 'react';
import { Typography, Box, Container } from '@mui/material';
import Navbar from '../Components/Navbar'; 
import './Home.css';

const Home = () => {
  return (
    <div className="home">
      {/* Navbar */}
      <Navbar /> {/* Reusing the shared Navbar component */}

      {/* Content Section */}
      <Container maxWidth="lg">
        <Box
          display="flex"
          flexDirection="column"
          justifyContent="center"
          alignItems="center"
          sx={{
            height: '70vh',
            padding: '20px',
            backgroundColor: 'rgba(255, 255, 255, 0.4)', // Matching transparency with the Navbar
            borderRadius: '30px', // Bubbly corners similar to Navbar
            boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)', // Optional: adds subtle shadow for depth
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            width: '60%', // Sets width and centers the box
            margin: 'auto' // Additional centering horizontally
          }}
        >
          {/* Main Title */}
          <Typography variant="h3" component="h1" gutterBottom sx={{ color: '#333' }}>
            Her Power, Her Past
          </Typography>
          {/* Short Description */}
          <Typography variant="body1" color="textSecondary" sx={{ color: '#555' }}>
            The lives of women weaved into the crevices of history.
          </Typography>
        </Box>
      </Container>
    </div>
  );
};

export default Home;
