import React from 'react';
import { Typography, Box, Container, Link, Grid, CardMedia, Divider } from '@mui/material';
import Navbar from '../Components/Navbar';
import './Blogs.css';

const Blogs = () => {
  const blogPosts = [
    {
      id: 1,
      title: "The Impact of Women in Technology",
      summary: "Explore the significant contributions of women in the tech industry and their ongoing challenges.",
      link: "/blogs/the-impact-of-women-in-technology",
      imageUrl: "/path-to-your-image.jpg"
    },
    {
      id: 2,
      title: "Historical Women Leaders",
      summary: "A look back at the women who shaped their nations and the world.",
      link: "/blogs/historical-women-leaders",
      imageUrl: "/path-to-another-image.jpg"
    }
  ];

  return (
    <div className="home">
      <Navbar />
      <Container maxWidth="lg">
        <Box className="centered-box" sx={{ width: '100%' }}>  // Adjusted for full width within the container
          {blogPosts.map((post, index) => (
            <Box key={post.id} sx={{ width: '100%', my: 2 }}> // Dynamic sizing based on content
              <Grid container spacing={2} alignItems="flex-start">  // Changed to flex-start for better alignment
                <Grid item xs={12} sm={4}>  // Responsive configuration for smaller screens
                  <CardMedia
                    component="img"
                    image={post.imageUrl}
                    alt={post.title}
                    sx={{ width: '100%', height: 'auto', maxHeight: 300 }}  // Responsive and aspect ratio maintained
                  />
                </Grid>
                <Grid item xs={12} sm={8}>
                  <Typography variant="h5" gutterBottom>
                    <Link href={post.link} color="inherit" style={{ textDecoration: 'none' }}>
                      {post.title}
                    </Link>
                  </Typography>
                  <Typography variant="body2">
                    {post.summary}
                  </Typography>
                </Grid>
              </Grid>
              {index !== blogPosts.length - 1 && <Divider />}
            </Box>
          ))}
        </Box>
      </Container>
    </div>
  );
};

export default Blogs;
